// component/MoviesList.js

import React, { useState, useEffect } from "react";
import Progress from "../component/Progress";

const URL_MOVIES = process.env.REACT_APP_ROOT_URL + "PizzasMovies";

const MoviesList = () => {

    const[movies, setMovies] = useState([]);
    useEffect( () => {
        searchMovies();
    }, [])

    const searchMovies = async() => {
        // fetch 
        const response = await fetch(URL_MOVIES + "?sort=-title&init=100", {
            method: "GET",
            headers: {
                "xc-auth": process.env.REACT_APP_NOCODB_KEY,
            },
        });
        const data = await response.json();
        console.log(data[0]);
        setMovies(data);

    }



    return (
        <>
           {movies?.length > 0 ?(
               <>
               <div className="row">
                   {movies.map((movie) => (
                       <li key={movie.id}>
                           <img src={movie.cover} style={{ width : "64px"}} />
                           {movie.title}
                        </li>
                   ))}
               </div>
                </>
               ) : (
                   <Progress />
               )
               }
        </>
    )


 };


export default MoviesList;
